												<>
													Approve {token0.symbol}
												</>
											}
											{token0 && token0Approved &&
												<>
													Unstake
												</>
											}
										</>
									}
									{working &&
										<>
											<Spinner />
										</>
									}
								</>
							}
							{!wallet.account &&
								<>
									Unstake
								</>
							}
						</Text>
					</Button>
				</Flex>
			</Flex>
		</>
	)
}

export default Earn
