import { mode } from '@chakra-ui/theme-tools'

export default {
	baseStyle: (props) => ({
		fontFamily: 'Button',
		_focus: {
			boxShadow: `0 0 0 3px
			${mode('var(--chakra-colors-accent-dark)',
			'var(--chakra-colors-accent-light)')(props)}`,
		},
	}),
	variants: {
		solid: (props) => ({
			color: mode('primary.dark',
				'primary.light')(props),
			_hover: {
				color: '#000000',
			},
		}),
		modalCentricLarge: () => ({
			background: 'transparent',
			width: '100%',
			minH: '114px',
			borderRadius: '20px',
			lineHeight: 'unset',
			cursor: 'pointer',
			padding: '1rem',
			_hover: {
				background: '#ffffff91',
			},
			_active: {
				background: '#ffffff91',
			},
			_focus: {
				background: '#ffffff91',
			},
		}),
		link: (props) => ({
			color: mode('primary.dark',
				'primary.light')(props),
			height: 'auto',
			_hover: {
				color: '#000000',
				textDecoration: 'none',
			},
		}),
		ghost: () => ({
			borderRadius: '12px',
			_hover: {
				background: 'transparent',
			},
			_active: {
				background: 'transparent',
			},
		}),
		ghostDark: () => ({
			color: '#4F4F4F',
			_hover: {
				background: 'transparent',
			},
			_active: {
				background: 'transparent',
			},
		}),
		ghostSelectable: () => ({
			p: '0',
			borderRadius: '0',
			_hover: {
				background: '#ffffff91',
			},
			_active: {
				background: '#ffffff50',
			},
			_focus: {
				background: '#ffffff30',
			},
		}),
	},
}
