/* eslint-disable no-unused-vars */
import { useQuery as useApolloQuery, gql } from '@apollo/client'
import defaults from '../common/defaults'

export const useUsdvMintedBurnt = (burnt = false, pollInterval = defaults.api.graphql.pollInterval) => {

	const amount = useQuery(`${tokenAddress}_erc20Balanceof_${wallet?.account}`,
	async () => {
		return await getERC20BalanceOf(
			tokenAddress,
			wallet?.account,
			defaults.network.provider,
		)
	}, {
		staleTime: defaults.api.staleTime,
		enabled: (!!wallet?.account && !!tokenAddress),
	},
)

return amount

}
