import React from 'react'
import { Button, Box, Image, ScaleFade, Tooltip } from '@chakra-ui/react'
import defaults from '../common/defaults'
import { prettifyNumber } from '../common/utils'
import { useUSDVprice } from '../hooks/useUSDVprice'

export const USDVpriceIndicator = () => {

	const price = useUSDVprice()

	return (
		<>
			<ScaleFade
				initialScale={0.9}
				in={stakingApr >= 0}>
				<Box textAlign={{ base: 'center', md: 'left' }}>
					<Tooltip
						hasArrow
						label='Press to cycle'
						color='black'
						openDelay={1325}
						placement='right'>
						<Badge
							_hover={{
								cursor: 'pointer',
							}}
							onClick={() => {
								if (daysApr === 7) {
									setDaysApr(14)
								}
								else if (daysApr === 14) {
									setDaysApr(30)
								}
								else if (daysApr === 30) {
									setDaysApr(90)
								}
								else if (daysApr === 90) {
									setDaysApr(180)
								}
								else if (daysApr === 180) {
									setDaysApr(365)
								}
								else if (daysApr === 365) {
									setDaysApr(7)
								}
							}}
							fontSize={{ base: '0.9rem', md: '1rem' }}
							colorScheme='accent'
						>{drawPeriod()} RETURN</Badge>
					</Tooltip>
				</Box>
				<Box
					fontSize={{ base: '1.1rem', md: '2.3rem', lg: '2.3rem' }}
					lineHeight='1.2'
					fontWeight='normal'
					mb='23px'
					textAlign={{ base: 'center', md: 'left' }}>
					{getPercentage(stakingApr)}
				</Box>
			</ScaleFade>
			<Button
				variant='ghost'
				fontSize={{ base: '0.65rem', sm: 'sm' }}
				display='inline-flex'
				alignItems='center'
				justifyContent='center'
				borderRadius='12px'
				background='#000'
				p='7px 10px'
				gridGap='13px'
				transform={{
					base: 'scale(0.86)',
					sm: 'scale(1)',
				}}
			>
				<Box
					d='flex'
					alignItems='center'
				>
					<Image
						width='24px'
						height='24px'
						mr='5px'
						src={defaults.usdv.logoURI}
						alt={`${defaults.usdv.name} token`}
					/>
					<Box
						as='h3'
						m='0'
						textTransform='capitalize'>
						<Box as='span' fontSize='1rem'>$</Box>
						{prettifyNumber(price, 0, 5, 'us')}
					</Box>
				</Box>
			</Button>
		</>
	)
}
