											w='100%'
											flexDir='row'
											justifyContent='space-between'
										>
											<FormLabel
												htmlFor='useLPTokens'
												fontWeight='bolder'
											>
											Use LP tokens instead
											</FormLabel>
											<Switch
												id='useLPTokens'
												size='lg'
												isChecked={useLPTokens}
												onChange={() => setUseLPTokens(!useLPTokens)}/>
										</FormControl>
									</Flex>
								</Flex>
							</Flex>

							<Flex
								flexDir='column'
								height='100%'
								width={{ base: '100%', md: '50%' }}
							>
								<Flex
									p={{ base: '1.8rem 0.6rem', md: '1.8rem 1.8rem 1.8rem 0.9rem' }}
									minH={{ base: '', md: '526.4px' }}
									justifyContent='space-between'
									gridGap='12px'
									flexDir='column'>

									{useBreakpointValue({
										base:	'',
										md: <PriceOverview
											bond={bond}
										/>,
									})}

									{useBreakpointValue({
										base:	'',
										md: <>
											{tabIndex == 0 &&
													<Breakdown
														value={inputAmount}
														useLPTokens={useLPTokens}
														treasuryBalance={treasuryBalance}
														maxPayout={maxPayout}
														bond={bond}
													/>
											}
											{tabIndex === 1 &&
													<Overview
														bondInfo={bondInfo}
														pendingPayout={pendingPayout}
													/>
											}
										</>,
									})}

									{tabIndex == 0 &&
										<Flex
											minH='76px'
											m='1.66rem 0'
											fontSize={{ base: '1.35rem', md: '1.5rem' }}
											fontWeight='bolder'
											justifyContent='center' alignItems='center' flexDir='column'
										>
											<Box
												minH={{ base: '32.4px', md: '36px' }}
											>
												{treasuryBalance &&
													<>
														{treasuryBalance.gt(defaults.bondConsideredSoldOutMinVader) &&
														<>
															{purchaseValue !== '' &&
																prettifyCurrency(
																	purchaseValue !== '' ? ethers.utils.formatUnits(purchaseValue, 18) : 0,
																	0,
																	2,
																	'VADER')
															}
														</>
														}
														{treasuryBalance.lte(defaults.bondConsideredSoldOutMinVader) &&
															<>
																Sold Out
															</>
														}
													</>
												}
											</Box>

											<Box
												as='h3'
												fontWeight='bold'
												textAlign='center'
												fontSize='1rem'
											>
												{treasuryBalance &&
													<>
														{treasuryBalance.gt(defaults.bondConsideredSoldOutMinVader) &&
															<>
																<Badge
																	as='div'
																	fontSize={{ base: '0.6rem', md: '0.75rem' }}
																	background='rgb(214, 188, 250)'
																	color='rgb(128, 41, 251)'
																>PURCHASE VALUE
																</Badge>
															</>
														}
														{treasuryBalance.lte(defaults.bondConsideredSoldOutMinVader) &&
															<>
																<Badge
																	as='div'
																	fontSize={{ base: '0.6rem', md: '0.75rem' }}
																	background='rgb(214, 188, 250)'
																	color='rgb(128, 41, 251)'
																>NOT AVAILABLE
																</Badge>
															</>
														}
													</>
												}
											</Box>
										</Flex>
									}

									{tabIndex == 1 &&
										<Flex
											minH='76px'
											m='1.66rem 0'
											fontSize={{ base: '1.35rem', md: '1.5rem' }}
											fontWeight='bolder'
											justifyContent='center' alignItems='center' flexDir='column'
										>
											<Box
												minH={{ base: '32.4px', md: '36px' }}
											>
												{pendingPayout && pendingPayout.gte(0) &&
													prettifyCurrency(
														ethers.utils.formatUnits(pendingPayout, defaults.vader.decimals),
														0,
														5,
														'VADER')
												}
											</Box>

											<Box
												as='h3'
												fontWeight='bold'
												textAlign='center'
												fontSize='1rem'
											>
												{pendingPayout && pendingPayout.gte(0) &&
												<Badge
													as='div'
													fontSize={{ base: '0.6rem', md: '0.75rem' }}
													background='rgb(214, 188, 250)'
													color='rgb(128, 41, 251)'
												>CLAIMABLE NOW
												</Badge>
												}
											</Box>
										</Flex>
									}

									<Flex justifyContent='center'>
										<Button
											size='lg'
											w='100%'
											mb={{ base: '0.9rem', md: '0' }}
											variant='solidRounded'
											disabled={working}
											onClick={() => submit()}
										>
											<Text fontWeight="bold">
												{wallet.account &&
													<>
														{!working &&
															<>
																{tabIndex === 0 &&
																	<>
																		{token0 && !token0Approved &&
																	<>
																		Approve {token0.symbol}
																	</>
																		}
																		{token0 && token0Approved &&
																	<>
																		Purchase
																	</>
																		}
																	</>
																}
																{tabIndex === 1 &&
																	<>
																		Claim
																	</>
																}
															</>
														}
														{working &&
															<>
																<Spinner />
															</>
														}
													</>
												}
												{!wallet.account &&
													<>
														{tabIndex === 0 &&
															<>
																Purchase
															</>
														}
														{tabIndex === 1 &&
															<>
																Claim
															</>
														}
													</>
												}
											</Text>
										</Button>
									</Flex>
								</Flex>
							</Flex>
						</Flex>
					</Flex>
				</Box>
			</>
		)
	}
	return (
		<Redirect to={'/bond'} />
	)
}

const PriceOverview = (props) => {

	PriceOverview.propTypes = {
		bond: PropTypes.any.isRequired,
	}

	const { data: bondPrice } = useBondPrice(props.bond?.[0]?.address)
	const [usdcEth] = useUniswapV2Price(defaults.address.uniswapV2.usdcEthPair)
	const [vaderEth] = useUniswapV2Price(defaults.address.uniswapV2.vaderEthPair)
	const [principalEth] = useUniswapV2Price(props.bond?.[0]?.principal?.address, true)

	return (
		<Flex>
			<Container p='0 6px 6px 2px'>
				<Box textAlign={{ base: 'center', md: 'left' }}>
					<Tag
						width='100%'
						minH={{ base: '77.6667px', md: '95.5167px' }}
						flexDir='column'
						size='lg'
						variant='outline'
						p='18px 0'
					>
						<Box
							fontSize={{ base: '0.87rem', md: '1rem' }}
						>
							Bond Price</Box>
						<TagLabel
							fontSize={{ base: '1.3rem', md: '2.1rem' }}
						>
							{bondPrice && usdcEth?.pairs?.[0]?.token0Price && principalEth?.principalPrice &&
								<>
									{prettifyCurrency(
										Number(ethers.utils.formatUnits(bondPrice, 18)) *
										(Number(usdcEth?.pairs?.[0]?.token0Price) * Number(principalEth?.principalPrice)),
										0, 5)}
								</>
							}
						</TagLabel>
					</Tag>
				</Box>
			</Container>

			<Container p='0 2px 6px 6px'>
				<Box textAlign={{ base: 'center', md: 'left' }}>
					<Tag
						width='100%'
						minH={{ base: 'auto', md: '95.5167px' }}
						flexDir='column'
						size='lg'
						variant='outline'
						p='18px 0'
					>
						<Box
							fontSize={{ base: '0.87rem', md: '1rem' }}
						>
							Market Price
						</Box>
						<TagLabel
							fontSize={{ base: '1.3rem', md: '2.1rem' }}
						>
							{!isNaN(Number(usdcEth?.pairs?.[0]?.token0Price) * Number(vaderEth?.pairs?.[0]?.token1Price)) &&
									<>
										{prettifyCurrency(
											(Number(usdcEth?.pairs?.[0]?.token0Price) * Number(vaderEth?.pairs?.[0]?.token1Price)),
											0,
											5,
										)}
									</>
							}
						</TagLabel>
					</Tag>
				</Box>
			</Container>
		</Flex>
	)
}

