import React from 'react'
import { Button, Box, Image, ScaleFade, Tooltip } from '@chakra-ui/react'
import defaults from '../common/defaults'
import { prettifyNumber } from '../common/utils'
import { useUSDVprice } from '../hooks/useUSDVprice'

export const USDVpriceIndicator = () => {

	const price = useUSDVprice()

	return (
		<>
			<ScaleFade
				initialScale={0.9}
				in={stakingApr >= 0}>
				<Box textAlign={{ base: 'center', md: 'left' }}>
					<Tooltip
						hasArrow
						label='Press to cycle'
						color='black'
						openDelay={1325}
						placement='right'>
						<Badge
							_hover={{
								cursor: 'pointer',
							}}
							onClick={() => {
								if (daysApr === 7) {
									setDaysApr(14)
								}
								else if (daysApr === 14) {
									setDaysApr(30)
								}
								else if (daysApr === 30) {
									setDaysApr(90)
								}
								else if (daysApr === 90) {
									setDaysApr(180)
								}
								else if (daysApr === 180) {
									setDaysApr(365)
								}
								else if (daysApr === 365) {
									setDaysApr(7)
								}
							}}
							fontSize={{ base: '0.9rem', md: '1rem' }}
							colorScheme='accent'
						>{drawPeriod()} RETURN</Badge>
					</Tooltip>
				</Box>
			</ScaleFade>
		</>
	)
}
