import React, { useRef, useState, useEffect } from 'react'
import PropTypes from 'prop-types'
import { useWallet } from 'use-wallet'
import { Menu, MenuButton, Button, Portal, MenuList,
	MenuItem, Flex, Image, useToast, MenuDivider, useBreakpointValue } from '@chakra-ui/react'
import { AddIcon, CloseIcon } from '@chakra-ui/icons'
import { prettifyAddress } from '../common/utils'
import Jazzicon from '@metamask/jazzicon'
import { connected } from '../messages'
import { WalletConnectionModal } from './WalletConnectionModal'
import defaults from '../common/defaults'

const AddTokenItem = (props) => {

	AddTokenItem.propTypes = {
		name: PropTypes.string.isRequired,
		icon: PropTypes.object.isRequired,
		wallet: PropTypes.object.isRequired,
		token: PropTypes.object.isRequired,
	}

	return (
		<MenuItem
			icon={props.icon}
			onClick={() => {
				try {
					props.wallet?.ethereum?.request({
						method: 'wallet_watchAsset',
						params: {
							type: 'ERC20',
							options: {
								address: props.token.address,
								symbol: props.token.symbol,
								decimals: props.token.decimals,
								image: props.token.logoURI,
							},
						},
					})
				}
				catch (err) {
					console.log(err)
				}
			}}
			{...props}
		>
			<Flex
				gridGap={'7px'}
			>
				{props.name}
				<Flex
					justifyContent='flex-start'
					fontWeight='bolder'>
					<Image
						width='24px'
						height='24px'
						mr='5px'
						src={props.token.logoURI}
						alt={`${props.token.name} token`}
					/>
					{`${props.token.symbol} into wallet`}
				</Flex>
			</Flex>
		</MenuItem>
	)
}

export const WalletConnectionToggle = props => {
	const initialText = 'Connect'
	const wallet = useWallet()
	const ref = useRef()
	const toast = useToast()
	const [working, setWorking] = useState(false)
	const [text, setText] = useState(initialText)
	const [isModalOpen, setIsModalOpen] = useState(false)
	const [isMenuOpen, setIsMenuOpen] = useState(false)

	const toggle = () => {
		if (!wallet.account) {
			setIsModalOpen(true)
		}
		else {
			setIsMenuOpen(true)
		}
	}

	const prettyAccount = useBreakpointValue({
		base: '',
		md: '',
		lg: prettifyAddress(wallet?.account),
	})

	useEffect(() => {
		if (wallet.account !== null) {
			setText(prettyAccount)
			ref.current.appendChild(
				Jazzicon(16, parseInt(wallet.account.slice(2, 10), 16)),
			).style.marginLeft = ml
			toast(connected)
		}
		return () => {
			if (wallet.account) {
				if (ref.current) ref.current.getElementsByTagName('div')[0].remove()
				setText(initialText)
			}
		}
	}, [wallet.account, prettyAccount])

	return (
		<>
			<Menu
				isOpen={isMenuOpen}
				onClose={() => setIsMenuOpen(false)}
				closeOnEsc={true}
				closeOnBlur={true}
				matchWidth={true}
				placement={'bottom-end'}
				autoSelect={false}
			>
				<MenuButton
					as={Button}
					size='md'
					minWidth='initial'
					fontSize={{ base: '0.65rem', sm: 'sm' }}
					variant='solidRounded'
					aria-label='Wallet Connection Status'
					isLoading={working}
					onClick={toggle}
					display='flex'
					flexDirection='row'
					ref={ref}
					{...props}
					style={{
						textAlign: 'center',
					}}
				>
					<span
						style={{
							order: '-1',
						}}
					>
						{text}
					</span>
				</MenuButton>
				<Portal>
					<MenuList
						zIndex={{ base: '2', md: '1' }}>
						<AddTokenItem
							name='Add'
							icon={<AddIcon layerStyle='menuIcon' />}
							wallet={wallet}
							token={defaults.vader}
						/>
						<AddTokenItem
							name='Add'
							icon={<AddIcon layerStyle='menuIcon' />}
							wallet={wallet}
							token={defaults.xvader}
						/>
						<AddTokenItem
							name='Add'
							icon={<AddIcon layerStyle='menuIcon' />}
							wallet={wallet}
							token={defaults.usdv}
						/>
						<MenuDivider />
						<MenuItem
							icon={<CloseIcon layerStyle='menuIcon' />}
							onClick={() => wallet?.reset()}
						>
      				Disconnect
						</MenuItem>
					</MenuList>
				</Portal>
			</Menu>
			<WalletConnectionModal
				isOpen={isModalOpen}
				onClose={() => setIsModalOpen(false) & setWorking(false)}
				setWorking={setWorking}
			/>
		</>
	)
}
