import React from 'react'
import { useLocation } from 'react-router-dom'
import { Flex } from '@chakra-ui/react'
import defaults from '../../common/defaults'
import { Link } from 'react-router-dom'
import { Logotype, WalletConnectionToggle } from '../../components'

export const Header = (props) => {

	const location = useLocation()
	const pages = [
		{
			name: 'Swap',
			text: 'Swap',
			link: '/',
		},
		{
			name: 'Liquidity',
			text: 'Liquidity',
			link: '/liquidity',
		},
		{
			name: 'Rewards',
			text: 'Rewards',
			link: '/rewards',
		},
	]

	const current = {
		background: '#835a81',
		borderRadius: '10px',
		fontWeight: '1000',
		color: '#fff',
	}

	return (
		<Flex
			justifyContent='space-between'
			alignItems='center'
			flexWrap='wrap'
			minH={defaults.layout.header.minHeight}
			{...props}>
			<Flex
				w='100%'
			>
				<Logotype />
			</Flex>
			<Flex
				order={{ base: '3', sm: '3', lg: '0' }}
				gridGap='12.3px'
			>
				{pages.map(p =>
					<Link
						key={p.name}
						to={p.link}
						style={ {
							color: 'rgb(213, 213, 213)',
							padding: '0.5rem 1.3rem',
							...(location.pathname === '/' && p.name === 'Swap' && current),
							...(p.link === location.pathname && current),
						}}
					>
						{p.text}
					</Link>)
				}
			</Flex>
			<Flex
				w='100%%'
 				justifyContent='flex-end'
				gridGap='12.3px'
			>
				<WalletConnectionToggle/>
			</Flex>
		</Flex>
	)
}
