import { useQuery as useApolloQuery, gql } from '@apollo/client'
import { useQuery } from 'react-query'
import { getVirtualPrice } from '../common/ethereum'
import { useERC20Balance } from './useERC20Balance'
import defaults from '../common/defaults'

export const useUSDVprice = (rpc = true, pollInterval = defaults.api.graphql.pollInterval, staleTime = defaults.api.staleTime) => {

	if (!rpc) {
		// GQL 2 DO
	}
	else {

		const balance = useERC20Balance(defaults.address.usdv, defaults.address.usdv3crvf)

		const virtualPrice = useQuery(`viretualPrice_${defaults.address.usdv3crvf}`,
			async () => {
				return await getVirtualPrice()
			}, {
				staleTime: defaults.api.staleTime,
			},
		)

		console.log(balance)
		return
	}

}
