		token: PropTypes.object.isRequired,
		unclaimed: PropTypes.object.isRequired,
		releaseTime: PropTypes.object.isRequired,
		now: PropTypes.object.isRequired,
	}

	return (
		<Flex
			flexDir='column'
			justifyContent='center'
			minH='141.6px'
		>
			<Text
				as='h4'
				fontSize='1.1rem'
				fontWeight='bolder'
				mr='0.66rem'
			>
				Overview
			</Text>

			<Flex
				flexDir='column'
				p='0 0.15rem'
				marginBottom='.7rem'
				opacity='0.87'
			>
				<Flex>
					<Container p='0'>
						<Box
							textAlign='left'
						>
							Total unclaimed
						</Box>
					</Container>
					<Container p='0'>
						<Box
							textAlign='right'
						>
							{prettifyCurrency(ethers.utils.formatEther(props.unclaimed), 0, 2, props.token.symbol)}
						</Box>
					</Container>
				</Flex>
				<Flex>
					<Container p='0'>
						<Box
							textAlign='left'>
