/* eslint-disable no-unused-vars */
import { BigNumber } from 'ethers'
import { useQuery } from 'react-query'
import defaults from '../common/defaults'
import { getMinter, getMinterDailyLimits, getCycleMints } from '../common/ethereum'

export const useMintLimitRemains = (staleTime = defaults.api.staleTime) => {

	const { data: minter } = useQuery('minter', async () => {
		return await getMinter()
	}, {
		staleTime: staleTime,
	},
	)

	const limits = useQuery('dailyLimits', async () => {
		return await getMinterDailyLimits(minter)
	}, {
		staleTime: staleTime,
		enabled: !!minter,
	},
	)

	const { data: cycleMints } = useQuery('cycleMints',
		async () => {
			return await getCycleMints(minter)
		}, {
			staleTime: defaults.api.staleTime,
		}, {
			staleTime: staleTime,
			enabled: !!minter,
		},
	)

	if(limits?.data?.[1] && cycleMints) {
		return limits?.data?.[1].sub(cycleMints)
	}
	else  {
		return BigNumber.from('0')
	}

}
