import React, { useState } from 'react'
import { TokenJazzicon } from '../../components'
import { Flex, InputGroup, Input, InputRightAddon, Box, Image } from '@chakra-ui/react'
import { ethers } from 'ethers'

export const SwapBox = () => {

	const [value, setValue] = useState(0)
	const [inputAmount, setInputAmount] = useState('')
	const [token0] = useState('')

	console.log(value)

	return (
		<Flex>
			<Flex
				layerStyle='inputLike'
			>
				<InputGroup>
					<Input
						variant='transparent'
						flex='1'
						fontSize='1.3rem'
						fontWeight='bold'
						placeholder='0.0'
						value={inputAmount}
						onChange={(e) => {
							if (isNaN(e.target.value)) {
								setInputAmount(prev => prev)
							}
							else {
								setInputAmount(String(e.target.value))
								if(Number(e.target.value) > 0) {
									try {
										setValue(ethers.utils.parseUnits(String(e.target.value), token0.decimals))
									}
									catch(err) {
										if (err.code === 'NUMERIC_FAULT') {
											console.log('num fault')
										}
									}
								}
							}
						}}/>
					<InputRightAddon
						width='auto'
						borderTopLeftRadius='0.375rem'
						borderBottomLeftRadius='0.375rem'
						paddingInlineStart='0.5rem'
						paddingInlineEnd='0.5rem'
					>
						<Flex
							cursor='default'
							zIndex='1'
						>
							<Box d='flex' alignItems='center'>
								{token0 && token0?.logoURI &&
										<Image
											width='24px'
											height='24px'
											borderRadius='50%'
											mr='5px'
											src={token0?.logoURI}
											alt={`${token0?.name} token`}
										/>
								}
								{token0?.address && !token0?.logoURI &&
										<TokenJazzicon address={token0.address} />
								}
								<Box
									as='h3'
									m='0'
									fontSize='1.02rem'
									fontWeight='bold'
									textTransform='capitalize'>{token0?.symbol}</Box>
							</Box>
						</Flex>
					</InputRightAddon>
				</InputGroup>
				<InputGroup>
					<Input
						variant='transparent'
						flex='1'
						fontSize='1.3rem'
						fontWeight='bold'
						placeholder='0.0'
						value={inputAmount}
						onChange={(e) => {
							if (isNaN(e.target.value)) {
								setInputAmount(prev => prev)
							}
							else {
								setInputAmount(String(e.target.value))
								if(Number(e.target.value) > 0) {
									try {
										setValue(ethers.utils.parseUnits(String(e.target.value), token0.decimals))
									}
									catch(err) {
										if (err.code === 'NUMERIC_FAULT') {
											console.log('num fault')
										}
									}
								}
							}
						}}/>
					<InputRightAddon
						width='auto'
						borderTopLeftRadius='0.375rem'
						borderBottomLeftRadius='0.375rem'
						paddingInlineStart='0.5rem'
						paddingInlineEnd='0.5rem'
					>
						<Flex
							cursor='default'
							zIndex='1'
						>
							<Box d='flex' alignItems='center'>
								{token0 && token0?.logoURI &&
										<Image
											width='24px'
											height='24px'
											borderRadius='50%'
											mr='5px'
											src={token0?.logoURI}
											alt={`${token0?.name} token`}
										/>
								}
								{token0?.address && !token0?.logoURI &&
										<TokenJazzicon address={token0.address} />
								}
								<Box
									as='h3'
									m='0'
									fontSize='1.02rem'
									fontWeight='bold'
									textTransform='capitalize'>{token0?.symbol}</Box>
							</Box>
						</Flex>
					</InputRightAddon>
				</InputGroup>
			</Flex>
		</Flex>
	)
}
